# Base class
class Vehicle:
    def __init__(self, brand, model, year):
        self.brand = brand
        self.model = model
        self.year = year

    def start(self):
        return f'{self.brand} {self.model} is starting...'

    def stop(self):
        return f'{self.brand} {self.model} has stopped.'

# Derived class (inherits from Vehicle)
class Car(Vehicle):
    def __init__(self, brand, model, year, doors):
        super().__init__(brand, model, year)  # Call parent constructor
        self.doors = doors

    def honk(self):
        return 'Beep beep!'

    # Override parent method
    def start(self):
        return f'{self.brand} {self.model} car is starting with a roar!'

class Motorcycle(Vehicle):
    def __init__(self, brand, model, year, engine_size):
        super().__init__(brand, model, year)
        self.engine_size = engine_size

    def rev_engine(self):
        return 'Vroom vroom!'

# Create instances
my_car = Car('Toyota', 'Camry', 2022, 4)
my_bike = Motorcycle('Harley', 'Sportster', 2021, 883)

print(my_car.start())  # Uses overridden method
print(my_bike.start())  # Uses inherited method
print(my_car.honk())

