    def __init__(self, name, employee_id, salary, programming_language):
        super().__init__(name, employee_id, salary)
        self.programming_language = programming_language

    def get_info(self):
        return f'Developer: {super().get_info()} - {self.programming_language} Specialist'

    def code_skill(self):
        return f'Expert in {self.programming_language}'

