import json

def save_grades(students, filename='grades.json'):
    try:
        with open(filename, 'w') as file:
            json.dump(students, file, indent=2)
        return 'Grades saved successfully'
    except Exception as e:
        return f'Error saving grades: {e}'

def load_grades(filename='grades.json'):
    try:
        with open(filename, 'r') as file:
            return json.load(file)
    except FileNotFoundError:
        return {}
    except Exception as e:
        return f'Error loading grades: {e}'

students = {
    'Alice': {'math': 85, 'science': 92},
    'Bob': {'math': 78, 'science': 88}
}

